var app = new Vue({
  el: '#app',
  data: {
    newTodo:{title:'',duedate:''},
    todos:[
      {
        id : '1',
        title : '增加 localStorage',
        duedate : '2018-06-11',
        completed: false
      },
      {
        id : '2',
        title : '增加 edit todos',
        duedate : '2018-06-11',
        completed: false
      },
      {
        id : '3',
        title : '增加 未完成事項數量',
        duedate : '2018-06-11',
        completed: false
      }
    ],
    visibiltiy: 'all'
  },
  methods:{
    addTodo: function(){
      var value = {
        title : this.newTodo.title.trim(),
        duedate : this.newTodo.duedate
      };
      var timestamp = Math.floor(Date.now());
      // console.log(value, timestamp);
      if( !value.title || !value.duedate){
        return false;
      }
      this.todos.push({
        id: timestamp,
        title: value.title,
        duedate: value.duedate,
        completed: false
      });
      this.newTodo = {
        title:'',
        duedate:''
      }
    },
    removeTodo: function(key) {
      this.todos.splice(key, 1);
    },
    editTodo: function(item) {
      console.log(item);
    }
  },
  computed: {
    filteredTodos: function(){
      var newTodos = [];
      if (this.visibiltiy === 'all'){
        newTodos = this.todos;  
      } else if (this.visibiltiy === 'processing'){
        var newTodos = [];
        this.todos.forEach(function(item){
          if (!item.completed) {
            newTodos.push(item);
          }
        })
      } else if (this.visibiltiy === 'completed'){
        
        this.todos.forEach(function(item){
          if (item.completed) {
            newTodos.push(item);
          }
        })
      }
      return newTodos;
    }
  }
})
